import React, { Component } from "react";
import { Switch, Route, BrowserRouter as Router } from "react-router-dom";
import "./App.scss";
import Employee from "./components/Employee/Employee";
// import EmployeeError from "./components/EmployeeError/EmployeeError";
import ProjectReview from "./components/ProjectReview/ProjectReview";

class App extends Component {
   constructor(props) {
      super(props);
      this.state = {
         employee: null,
         isLoading: true,
      };
   }

   componentDidMount() {      setTimeout(() => {
         fetch("/api/*", { method: "GET" })
            .then((response) => {
               if (response.ok) {
                  return response.clone().json();
               }
            })
            .then((result) => {
               this.setState({ employee: result });
            })
            .then(() => {
               this.setState({ isLoading: false });
            })
            .catch((err) => console.log(err));
      }, 5000);
   }

   render() {
      const employee = this.state.employee;

      return (
         <div className="App">
            <div className="content">
               {this.state.isLoading ? (
                  <h1>LOADING</h1>
               ) : (
                  <Router>
                     <Switch>
                        <Route
                           exact
                           path="/"
                           render={() => <Employee employee={employee} />}
                        />
                     </Switch>
                     <Switch>
                        <Route
                           exact
                           path="/:title"
                           render={(props) => (
                              <ProjectReview {...props} employee={employee} />
                           )}
                        />
                     </Switch>
                  </Router>
               )}
            </div>
         </div>
      );
   }
}

export default App;
