import React, { Component } from "react";
import { Switch, Route, BrowserRouter as Router } from "react-router-dom";
import "./App.scss";
import Employee from "./components/Employee/Employee";
// import EmployeeError from "./components/EmployeeError/EmployeeError";
import ProjectReview from "./components/ProjectReview/ProjectReview";

class App extends Component {
   constructor(props) {
      super(props);
      this.state = {
         employee: null,
      };
   }

   componentDidMount() {
      fetch("/")
         .then((response) => response.json())
         .then((result) => this.setState({ employee: result }));
   }

   render() {
      const employee = {
         info: {
            name: "anusha",
            surname: "pasunuti",
            about: ` Lorem ipsum dolor sit amet consectetur, adipisicing
                        elit. Consequuntur dolorem sint dolores obcaecati ullam
                        est perferendis sequi non. Incidunt nostrum, praesentium
                        ea placeat voluptatibus ipsa excepturi ex sequi ratione
                        unde! `,
            styles: [
               "example",
               "example",
               "example",
               "example",
               "example",
               "example",
               "example",
               "example",
            ],
         },
         projects: [
            {
               category: "horeca",
               title: "view from the top",
               description: `Lorem ipsum dolor sit amet consectetur, adipisicing
                        elit. Consequuntur dolorem sint dolores obcaecati ullam
                        est perferendis sequi non. Incidunt nostrum, praesentium
                        ea placeat voluptatibus ipsa excepturi ex sequi ratione
                        unde!  Lorem ipsum dolor sit amet consectetur, adipisicing.
