import React from "react";
import "./Photo.scss";

const Photo = (props) => {
   const name =
      props.employeeName.toUpperCase().trim() +
      props.employeeSurname.toUpperCase().trim();
   const photo = require(`../../../../../img/employees/${name}/${name}.png`);

   return (
      <div className="employee-photo">
         <img src={photo} alt="employee" />
      </div>
   );
};

export default Photo;
